import { Component, OnInit } from "@angular/core";
import { MarketplaceApiService } from "src/app/core/marketplace-api/marketplace-api.service";
import { OfferModel } from "src/app/core/marketplace-api/models/offer.model";

@Component({
  selector: "app-offer-list",
  templateUrl: "./offer-list.component.html",
  styleUrls: ["./offer-list.component.scss"],
})
export class OfferListComponent implements OnInit {
  pageSize = 200;
  page = 1;
  offers: OfferModel[] = [];
  total = 0;
  totalPages = 0;

  constructor(public markeplaceApiService: MarketplaceApiService) {}

  ngOnInit(): void {
    this.markeplaceApiService.getOffersCount().subscribe((data) => {
      this.total = data;
      this.totalPages = Math.round(this.total / this.pageSize);
    });

    this.markeplaceApiService.getOffers(this.page, this.pageSize).subscribe({
      next: (offers: OfferModel[]) => {
        this.offers = offers;
      },
    });
  }

  getOffersPage(page,pageSize: number) {
    this.markeplaceApiService.getOffers(page, pageSize).subscribe({
      next: (offers: OfferModel[]) => {
        this.offers = offers;
      },
    });
  }
}
<div *ngFor="let offer of offers">
  <h2>{{ offer.id }}</h2>
  <app-offer [offer]="offer"></app-offer>
</div>
<h1>{{totalPages}}</h1>
<nav aria-label="Page navigation example">
  <ul class="pagination">
    <li class="page-item"><a class="page-link">Previous</a></li>
    <li class="page-item" (click)="getOffersPage(page,item)"  *ngFor="let item of totalPages | enumerate "><a class="page-link">{{item}}</a></li>

    <li class="page-item"><a class="page-link">Next</a></li>
  </ul>
</nav>
