import { Component, OnInit } from "@angular/core";
import { MarketplaceApiService } from "src/app/core/marketplace-api/marketplace-api.service";
import { OfferModel } from "src/app/core/marketplace-api/models/offer.model";

@Component({
  selector: "app-offer-list",
  templateUrl: "./offer-list.component.html",
  styleUrls: ["./offer-list.component.scss"],
})
export class OfferListComponent implements OnInit {
  pageSize = 10;
  page = 10;
  offers: OfferModel[] = [];
  total = 0;
  totalPages = 0;
  numbers: number[];


  constructor(public markeplaceApiService: MarketplaceApiService) {}

  ngOnInit(): void {
    this.markeplaceApiService.getOffersCount().subscribe((data) => {
      this.total = data;
      this.totalPages = Math.round(this.total / this.pageSize);
      this.numbers =  Array.from({length:this.page},(v,k)=>k+1);
    });

    this.markeplaceApiService.getOffers(this.page, this.pageSize).subscribe({
      next: (offers: OfferModel[]) => {
        this.offers = offers;
      },
    });
  }

  getOffersPage(page:number,pageSize:number) {
    this.markeplaceApiService.getOffers(page, pageSize).subscribe({
      next: (offers: OfferModel[]) => {
        this.offers = offers;
      },
    });
  }

  getNextOffersPage(page:number){
    if (this.numbers[this.page-1]=== this.totalPages) {
      return;
    }
    this.numbers = this.numbers.map(n => n + 1);
    this.getOffersPage(this.pageSize,this.numbers[this.page-1]);
  }

  getPreviousOffersPage(page:number){
    if (this.numbers[0]=== 1) {
      return;
    }
    this.numbers = this.numbers.map(n => n - 1);
    this.getOffersPage(this.pageSize,this.numbers[0]);
  }


  getFirstOffersPage(){
    if (this.numbers[0]=== 1) {
      return;
    }
    this.numbers =  Array.from({length:this.page},(v,k)=>k+1);
  }

  getLastOffersPage(){
    if (this.numbers[this.page-1]=== this.totalPages) {
      return;
    }
    const lastNumbers = this.totalPages - 10;
    this.getFirstOffersPage();
    this.numbers = this.numbers.map(n => n + lastNumbers);

  }
}

<div class="container">
  <div class="row" >
    <div *ngFor="let offer of offers" class="col-lg-3 col-md-6 col-xs-12 pt-3">

      <app-offer [offer]="offer"></app-offer>
    </div>
  </div>
</div>
<div class="d-flex justify-content-center mt-4">
  <nav aria-label="Page navigation example">
    <ul class="pagination">
      <li class="page-item" (click)="getFirstOffersPage()" ><a class="page-link">First</a></li>
      <li class="page-item" (click)="getPreviousOffersPage(page)" ><a class="page-link">Previous</a></li>
      <li class="page-item"  (click)="getOffersPage(page,item)"  *ngFor="let item of numbers "><a class="page-link">{{item}}</a></li>
      <li class="page-item" (click)="getNextOffersPage(page)"><a class="page-link">Next</a></li>
      <li class="page-item" (click)="getLastOffersPage()"><a class="page-link">Last</a></li>
    </ul>
  </nav>
</div>
