import {Component, Input, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, UntypedFormGroup, Validators} from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { MarketplaceApiService } from 'src/app/core/marketplace-api/marketplace-api.service';
import { CategoryModel } from 'src/app/core/marketplace-api/models/category.model';
import { OfferModel } from 'src/app/core/marketplace-api/models/offer.model';
import { MatSnackBar } from '@angular/material/snack-bar';

@Component({
  selector: 'app-offer-creation',
  templateUrl: './offer-creation.component.html',
  styleUrls: ['./offer-creation.component.scss']
})
export class OfferCreationComponent implements OnInit {

  // offerForm: UntypedFormGroup;

  @Input()
  categories: string[];
  form: FormGroup;

  constructor(private formBuilder: FormBuilder, public markeplaceApiService: MarketplaceApiService, public dialogRef: MatDialogRef<OfferCreationComponent>, private snackBar: MatSnackBar) {

  }
  ngOnInit(): void {
    this.form = this.formBuilder.group({
      title: ['', Validators.required],
      picture: ['', Validators.required],
      description: ['', Validators.required],
      location: ['', Validators.required],
      categoryId: ['', Validators.required]
    });
  }

  onSubmit() {
    const offer =  new OfferModel();
    if (this.form.valid) {
      offer.category =  new CategoryModel();
      offer.categoryId = this.form.value.categoryId;
      offer.description = this.form.value.description;
      offer.location = this.form.value.location;
      offer.pictureUrl = this.form.value.picture;
      offer.title = this.form.value.title;


      this.markeplaceApiService.addOffer(offer).subscribe({next:(data=>{
        if (data===1) {
          this.showOfferCreatedAlert()
        }

      })})
    } 
    this.markeplaceApiService.addOffer(offer)
  }

  closeDialog(): void {
    this.dialogRef.close();
  }

  showOfferCreatedAlert(): void {
    this.snackBar.open('The offer has been created', 'Close', {
      duration: 3000, // Duration in milliseconds
    });
  }
}
<div class="container">
  <form [formGroup]="form" (ngSubmit)="onSubmit()" class="w-100">
    <div class="form-group">
      <label for="title">Title</label>
      <input
        type="text"
        class="form-control"
        id="title"
        formControlName="title"
      />
      <div
        *ngIf="form.get('title').invalid && form.get('title').touched"
        class="text-danger"
      >
        <div *ngIf="form.get('title').errors?.required">Title is required</div>
      </div>
    </div>
    <div class="form-group">
      <label for="picture">Picture url</label>
      <input
        type="text"
        class="form-control"
        id="picture"
        formControlName="picture"
      />
      <div
        *ngIf="form.get('picture').invalid && form.get('picture').touched"
        class="text-danger"
      >
        <div *ngIf="form.get('picture').errors?.required">
          Picture url is required
        </div>
      </div>
    </div>
    <div class="form-group">
      <label for="description">Description</label>
      <textarea
        class="form-control"
        id="description"
        formControlName="description"
      ></textarea>
      <div
        *ngIf="
          form.get('description').invalid && form.get('description').touched
        "
        class="text-danger"
      >
        <div *ngIf="form.get('description').errors?.required">
          Description is required
        </div>
      </div>
    </div>
    <div class="form-group">
      <label for="location">Location</label>
      <input
        type="text"
        class="form-control"
        id="location"
        formControlName="location"
      />
      <div
        *ngIf="form.get('location').invalid && form.get('location').touched"
        class="text-danger"
      >
        <div *ngIf="form.get('location').errors?.required">
          Location is required
        </div>
      </div>
    </div>
    <div class="form-group">
      <label for="categoryId">Category</label>
      <select class="form-control" id="categoryId" formControlName="categoryId">
        <option value="">Select a category</option>
        <option value="1">Category 1</option>
        <option value="2">Category 2</option>
        <option value="3">Category 3</option>
      </select>
      <div
        *ngIf="form.get('categoryId').invalid && form.get('categoryId').touched"
        class="text-danger"
      >
        <div *ngIf="form.get('categoryId').errors?.required">
          Category is required
        </div>
      </div>
    </div>
    <div class="d-flex justify-content-center pt-3">
      <button mat-raised-button mat-dialog-close color="primary"      type="submit"
      [disabled]="form.invalid" class="pr-2">Submit</button>
      <button mat-raised-button mat-dialog-close color="accent">Close</button>
    </div>
  </form>
</div>
